mapper.select:
SELECT mem_nick
  FROM MEMBER
 WHERE mem_id = #{regId};

mapper.insert:
		INSERT INTO SURVEY( surv_title
                           ,surv_desc
                           ,reg_id
                           ,reg_date
                           ,mod_date
                           ,useYn
                          )
                    VALUES( #{survTitle}
                    	, #{survDesc}
                    	, #{regId} 
                    	, GETDATE()
                    	, GETDATE()
                    	, #{useYn}
                    	);

	
    	SELECT ISNULL(MAX(surv_no),0) 
    	  FROM SURVEY;
    
	 INSERT INTO SURVQUST( surv_no
                       ,qust_seq
                       ,qust_cont
                       ,qust_type
                      )
                VALUES ( #{survNo}
                		, #{qustSeq}
                		, #{qustCont}
                		, #{qustType}
                		)

		
        	SELECT ISNULL(MAX(qust_no),0) FROM SURVQUST;
        
		INSERT INTO QUSTOPT( qust_no
                           ,opt_seq
                           ,opt_cont
                          )
                    VALUES ( #{qustNo}
                    		, #{optSeq}
                    		, #{optCont}
                    		)

mapper.update:
UPDATE SURVEY
   SET delYn = 'Y'
     , useYn = 'N'
 WHERE surv_no = #{survNo}
 ;
