mapper.select:
SELECT survNo, survTitle, regDate, modDate, memNick, rownum FROM (SELECT s.surv_no as survNo, s.surv_title as survTitle, s.reg_id as regId, s.reg_date as regDate, s.mod_date as modDate, m.mem_nick as memNick, ROW_NUMBER() OVER (ORDER BY s.mod_date desc, s.surv_no desc) as rownum FROM Survey as s LEFT JOIN MEMBER as m ON s.reg_id = m.mem_id WHERE useYn = 'Y' AND delYn = 'N'
) A
WHERE rownum BETWEEN #{firstRecordIndex} AND #{lastRecordIndex}
;
