mapper.select:
SELECT survNo, survTitle, regId, regDate, modDate, rownum FROM (SELECT surv_no as survNo, surv_title as survTitle, reg_id as regId, reg_date as regDate, mod_date as modDate, ROW_NUMBER() OVER (ORDER BY mod_date desc, surv_no desc) as rownum FROM Survey WHERE useYn = 'Y' AND delYn = 'N'
) A
WHERE rownum BETWEEN #{firstRecordIndex} AND #{lastRecordIndex}
;
